def find_motif_in_dna(dna, motif):
    list_position_motif = []
    for i in range(len(dna)):
        if dna[i:i + len(motif)] == motif:
            list_position_motif.append(i + 1)
    return list_position_motif


dna = ""
motif = "ATGAGGGAT"

result = ""
for i in find_motif_in_dna(dna, motif):
    result += str(i) + " "

print(result)
